import React from 'react';


const MultiPlayerData = async(props) => {
    debugger
    const [open, setOpen] = React.useState(false);

  const handleClickToOpen = () => {
    setOpen(true);
  };

  const handleToClose = () => {
    setOpen(false);
  };
    if (props.length===3){
        debugger
        const resp = await axios.get(`http://localhost:4000/multi_player?board=${props[0]}&reset=true`)    
        console.log('reset',resp)
    }
    else {
        const resp = await axios.get(`http://localhost:4000/multi_player?board=${props}`)
        debugger
        console.log('chance',resp)
        if (resp.data.game_result.result){
            handleClickToOpen()
            return(
                <>
                    {/* {alert("game OVer....",resp.data.game_result.result)} */}
                    <div stlye={{}}>
        {/* <h4>How to create Dialog Box in ReactJS?</h4> */}
      {/* <Button variant="outlined" color="primary" 
              onClick={handleClickToOpen}>
        Open Demo Dialog
      </Button> */}
                    <Dialog open={open} onClose={handleToClose}>
                        <DialogTitle>{resp.data.game_result.result}</DialogTitle>
                        <DialogContent>
                        <DialogContentText>
                            Game Over
                        </DialogContentText>
                        </DialogContent>
                        <DialogActions>
                        <Button onClick={handleToClose} 
                                color="primary" autoFocus>
                            Close
                        </Button>
                        </DialogActions>
                    </Dialog>
                    </div>
                </>
            )
        }
    }

}

