    }
    if (props.length===2){
        const resp = await axios.get(`http://localhost:4000/single_player?board=${props[0]}&reset=true`)    
        console.log('reset resp',resp)
    }
    else{
        const resp = await axios.get(`http://localhost:4000/single_player?board=${props}`)
        debugger
        console.log("chance resp",resp)
        // console.warn('Board = ',resp.data.current_board.board)
        boardData["board"] = resp.data.current_board.board
        console.log('boardData= ',boardData)
    }
    debugger
