import { render } from '@testing-library/react';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Main from './main';

const Dashboard = (propScreen) => {
    const [screen,setScreen] = useState("")
    // setScreen(propScreen)

    useEffect(()=>{
        if (propScreen["props"].length === 1) {
            debugger
            setScreen("")
        }
    },[])



        const handleChange = (userChoice) => {
            setScreen(userChoice)
            render(
                <>
            {/* <Main Uchoice={userChoice}/> */}
            </>
        )
    }


    return(
        <>
        {screen === "" ? (<>
        <h1>Dashboard</h1><hr />
        <h1>Tic Tac Toe</h1><hr style={{width:"20%", marginLeft:"40%"}}/>
        <div className='container'>
            <Link onClick={()=>{handleChange("single_player")}} className='btn btn-warning'>Single Player</Link><hr style={{width:"10%", marginLeft:"45%"}} />
            <Link onClick={()=>{handleChange("multi_player")}} className='btn btn-danger'>Multi Player</Link>
        </div>
        </>):<Main Uchoice={screen}/>}
        </>
    )
}


export default Dashboard;
