    const checkString = (e) => {
        e.preventDefault()
        // debugger
        const buttonValue = e.target.name - 1
        console.log(buttonValue)
        const stringArry = gameString.split("")
        setArryOfString(stringArry)
        stringArry[buttonValue] = "X"
        const stringBundle = stringArry.join("")
        setGameString(stringBundle)
        debugger


        // let stringarry = gameString.split("")
