        // })
    }


// debugger
    const dataArry = ["o",'x','x',"o","o","o","x","x","o"]

    const buttonProperty = () => {
        return(
            <>
            {dataArry.map((value)=>{
                debugger
               if (value==="o"){ (<></>)} 
               else{(<><button className='btn btn-danger'>X</button></>)}
            })}
            {/* {
                choice === "O" ? (<><button className='btn btn-success'>O</button></>) : (<><button className='btn btn-danger'>X</button></>)
            } */}
            </>
        )
    }

    const buttonName = ""

    return(
        <>
        <h1>Main Screen</h1>
        <div class="container-fluid">
                <div className=' col-12 '>
                    <h3>Tic tac Toe</h3>
                </div>
                <div style={{marginTop:"8%", marginLeft:"25%", marginRight:"25%",}} className='box row'>
                    {arryOfString.map((value)=>{
                        if(value === " "){ buttonName = " " }
