                    <div className='box_element col-sm border-1'>
                        <div className='element row-1'><Link id="square1" name="1" className='square' onClick={checkString}></Link>{buttonName}</div>
                        <div className='row-2 element'><Link id="square1" name="4" className='square' onClick={checkString}></Link>{buttonName}</div>
                        <div className='row-3 element'><Link id="square1" name="7" className='square' onClick={checkString}></Link></div>
                    </div>
                        {/* <hr style={{width:"100%",marginTop:"-10%"}} /> */}
