                        <div className='row-1 element'><Link id="square1" name="2" className='square' onClick={checkString}></Link></div>
                        <div className='row-2 element'><Link id="square1"name="5" className='square' onClick={checkString}></Link></div>
                        <div className='row-3 element'><Link id="square1" name="8" className='square' onClick={checkString}></Link></div>

                    </div>
                    {/* <hr style={{width:"100%",marginTop:"-10%"}} /> */}
                    <div className='box_element col-sm'>
                        <div className='row-1 element'><Link id="square1" name="3" className='square' onClick={checkString}></Link></div>
                        <div className='row-2 element'><Link id="square1" name="6" className='square' onClick={checkString}></Link></div>
                        <div className='row-3 element'><Link id="square1" name="9" className='square' onClick={checkString}></Link></div>
                    </div>

                </div>

        </div>

       </>
    )
}

export default Main;
