import React, { useState } from 'react';
import './main.css'
const Main = () => {

    const [choice,setChoice] = useState("O")

    const buttonProperty = () => {
        return(
            <>
            {
                choice === "O" ? (<><button className='btn btn-success'>O</button></>) : (<><button className='btn btn-danger'>X</button></>)
            }
            </>
        )
    }


    return(
        <>
        <h1>Main Screen</h1>
        <div class="container-fluid">
                <div className=' col-12 '>
                    <h3>Tic tac Toe</h3>
                </div>
                <div style={{marginTop:"8%", marginLeft:"25%", marginRight:"25%",}} className='box row'>
                    <div className='box_element col-sm border-1'>
                        <div className='element row-1'>{buttonProperty()}</div>
                        <div className='row-2 element'><button className="btn">{choice}</button></div>
                        <div className='row-3 element'><button className="btn">{choice}</button></div>
                    </div>
                        {/* <hr style={{width:"100%",marginTop:"-10%"}} /> */}
                    <div className='box_element col-sm'>
                        <div className='row-1 element'><button className="btn">{choice}</button></div>
                        <div className='row-2 element'><button className="btn">{choice}</button></div>
                        <div className='row-3 element'><button className="btn">{choice}</button></div>

                    </div>
                    {/* <hr style={{width:"100%",marginTop:"-10%"}} /> */}
                    <div className='box_element col-sm'>
                        <div className='row-1 element'><button className="btn">{choice}</button></div>
                        <div className='row-2 element'><button className="btn">{choice}</button></div>
                        <div className='row-3 element'><button className="btn">{choice}</button></div>
                    </div>

                </div>

        </div>

       </>
    )
}

export default Main;
